import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/navbar';

const PersonalInformation = () => {
  const [userData, setUserData] = useState({full_name: '', email: '', address: {country: '', city: '', street: '', number: '', zipcode: ''}});
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    function getCookie(val) {
      const cookies = document.cookie.split('; ');
      for (const cookie of cookies) {
        const [cookieName, cookieValue] = cookie.split('=');
        if (cookieName === val) {
          return decodeURIComponent(cookieValue);
        }
      }
      return null;
    }
    const userEmail = getCookie('email');
    if (userEmail) {
      fetchUserData(userEmail);
    }
  }, []);

  const fetchUserData = async (email) => {
    try {
      const response = await axios.get(`http://localhost:6500/${email}`);
      setUserData(response.data.user);
      console.log('User data:', response.data.user);
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = async () => {
    try {
      await axios.put(`http://localhost:6500/user/${userData.email}`, userData);
      alert('User data updated successfully');
      setEditMode(false);
    } catch (error) {
      console.error('Error updating user data:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData(prevUserData => ({
      ...prevUserData,
      [name]: value,
      address: {
        ...prevUserData.address, // Preserve existing address properties
        [name]: value // Update the nested property
      }
    }));
    console.log('User data:', userData);
  };


  return (
    <div>
      <Navbar />
      <div>
        <h1>Personal Information</h1>
        <div>
          <form>
            <label htmlFor="fullName">Full Name:</label>
            <input
              type="text"
              id="fullName"
              name="fullName"
              value={userData.full_name}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="email">Email:</label>
            <input
              type="text"
              id="email"
              name="email"
              value={userData.email}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="email">Email:</label>
            <input
              type="text"
              id="email"
              name="email"
              value={userData.email}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="address">Country:</label>
            <input
              type="text"
              id="address"
              name="address"
              value={userData.address.country}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="address">City:</label>
            <input
              type="text"
              id="address"
              name="address"
              value={userData.address.city}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="address">Street:</label>
            <input
              type="text"
              id="address"
              name="address"
              value={userData.address.street}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="address">Number:</label>
            <input
              type="text"
              id="address"
              name="address"
              value={userData.address.number}
              onChange={handleInputChange}
              disabled={!editMode}/>
            <label htmlFor="address">Zipcode:</label>
            <input
              type="text"
              id="address"
              name="address"
              value={userData.address.zipcode}
              onChange={handleInputChange}
              disabled={!editMode}/>
          </form>
          {editMode ? (
            <button onClick={handleSaveClick}>Save</button>
          ) : (
            <button onClick={handleEditClick}>Edit</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default PersonalInformation;
