import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../../components/navbar';

const Checkout = () => {
  const [fullName, setFullName] = useState('');
  const [phone, setPhone] = useState('');
  const [country, setCountry] = useState('');
  const [city, setCity] = useState('');
  const [CreditCardNumber, setCreditCardNumber] = useState('');
  const [street, setStreet] = useState('');
  const [number, setNumber] = useState('');
  const [apt, setApt] = useState('');
  const [CVV, setCVV] = useState('');


    const navigate = useNavigate();
  const handlePayment = () => {
    if (!fullName || !phone || !country || !city ||street ||number|| !CreditCardNumber || !CVV) {
        alert('Please fill in all required fields.');
        return;
      }
      if (CVV.length !== 3)  {
        alert('Please enter a valid CVV.');
        return;
      }
      const creditCardRegex = /^\d{16}$/;
      if (!creditCardRegex.test(CreditCardNumber)) {
        alert('Please enter a valid credit card number.');
        return;
      }
    // Perform payment processing logic here

    // Display payment completed message
    alert('Payment completed');

    // Redirect to /shop
    navigate("/checkout");
    };

  return (
    <div>
    <Navbar />
      <h2>Checkout</h2>
      <form>
        <label htmlFor="fullName">Full Name:</label>
        <input
          type="text"
          id="fullName"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
        />

        <label htmlFor="address">Country:</label>
        <input
          type="text"
          id="country"
          value={country}
          onChange={(e) => setCountry(e.target.value)}
        />
        <label htmlFor="address">City:</label>
        <input
          type="text"
          id="city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <label htmlFor="address">Street:</label>
        <input
          type="text"
          id="street"
          value={street}
          onChange={(e) => setStreet(e.target.value)}
        />
        <label htmlFor="address">Number:</label>
        <input
          type="text"
          id="number"
          value={number}
          onChange={(e) => setNumber(e.target.value)}
        />
        <label htmlFor="address">Apartment:</label>
        <input
          type="text"
          id="apt"
          value={apt}
          placeholder='if there is no apartment number, leave it empty.'
          onChange={(e) => setApt(e.target.value)}
        />
        <label htmlFor="phone">Phone:</label>
        <input
          type="text"
          id="phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />

        <label htmlFor="creditCard">Credit Card Number:</label>
        <input
          type="text"
          id="creditCard"
          value={CreditCardNumber}
          onChange={(e) => setCreditCardNumber(e.target.value)}
        />
        <label htmlFor="CVV">CVV :</label>
        <input
          type="text"
          id="CVV"
          value={CVV}
          placeholder='3 digits on the back of your card.'
          onChange={(e) => setCVV(e.target.value)}
        />

        <button type="button" onClick={handlePayment}>
          Make a Payment
        </button>
      </form>
    </div>
  );
};

export default Checkout;
