import React, { useContext } from "react";
import { ShopContext } from "../context/shop-context";

export const CartItem = (props) => {
  const { prod_id, name, price, image } = props.data;
  const { cartItems, addToCart, removeFromCart } = useContext(ShopContext);

  // Get the quantity of this item from the cartItems context
  const quantity = cartItems[prod_id] || 0;

  // Delete the product item card from the page when quantity is zero
  if (quantity === 0) {
    return null;
  }

  return (
    <div className="cartItem">
      <img src={image} alt={name} />
      <div className="description">
        <p>
          <b>{name}</b>
        </p>
        <p> Price: ${price}</p>
        <div className="countHandler">
          {/* Button to decrease quantity */}
          <button onClick={() => removeFromCart(prod_id)}> - </button>
          {/* Display the quantity */}
          <span>{quantity}</span>
          {/* Button to increase quantity */}
          <button onClick={() => addToCart(prod_id)}> + </button>
        </div>
      </div>
    </div>
  );
};
