import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import emailjs from '@emailjs/browser';

const ForgotPassword = () => {
    const [code,Setcode] = useState('');
    const [email,SetEmail] = useState('');
    const codes = '';
    const navigate = useNavigate();
    function generateRandomCode() {
        const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()_+{}[]|;:<>,.?/~';
        let code = '';
        const codeLength = 6;

        for (let i = 0; i < codeLength; i++) {
          const randomIndex = Math.floor(Math.random() * characters.length);
          code += characters.charAt(randomIndex);
        }

        return code;
      }
    const handelcon = (e) => {
        emailjs.init("98g7Qzscyfz-S-J7p");
        const serviceId = "service_lxiaq84";
        const templateId = "template_en7libv";
        codes = generateRandomCode();
        emailjs.send(serviceId, templateId, {
            email: email,
            code: codes
        });
        alert('conformation code sent, please check your email')
    }
    const handelSignin = (e) => {
        if (code === codes){
            alert('conformation code verified')
            navigate('/');
        }
        else
        {
            alert('code is incorrect')
        }
    }
    const handeleChange = (e) => {
        if(e.target.name === 'email')
        {
            SetEmail(e.target.value);
        }
        if(e.target.name === 'code')
        {
            Setcode(e.target.value);
            console.log(code);
        }
    }
  return (
    <div>
        <div style={{marginTop:'20rem'}}>
            <form onSubmit={handelcon}>
                <input type="text" placeholder='enter your email' onChange={{handeleChange}} name='email' />
                <button style={{marginTop:'1rem'}} onChange={{handeleChange}} name='code'>send conformation code</button>
            </form>
        </div>
        <div style={{marginTop:'2.5rem'}}>
            <form onSubmit={handelSignin}>
                <input type="text" placeholder='enter conformation code ' name='code'/>
                <button style={{marginTop:'1rem'}}>submit</button>
            </form>
        </div>
    </div>
  );
};

export default ForgotPassword;
