import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/navbar';

const PersonalInformation = () => {
const [full_name, setFull_name] = useState('');
const [email, setEmail] = useState('');
const [password, setPassword] = useState('');
const [country, setCountry] = useState('');
const [city, setCity] = useState('');
const [street, setStreet] = useState('');
const [number, setNumber] = useState('');
const [zipcode, setZipcode] = useState('');
const [userData, setUserData] = useState({});
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    function getCookie(val) {
      const cookies = document.cookie.split('; ');
      for (const cookie of cookies) {
        const [cookieName, cookieValue] = cookie.split('=');
        if (cookieName === val) {
          return decodeURIComponent(cookieValue);
        }
      }
      return null;
    }
    const userEmail = getCookie('email');
    if (userEmail) {
      fetchUserData(userEmail);
    }
  }, []);


  const fetchUserData = async (email) => {
    try {
      const response = await axios.get(`http://localhost:6500/${email}`);
      setUserData(response.data.user);
      setFull_name(response.data.user.full_name);
        setEmail(response.data.user.email);
        setPassword(response.data.user.password);
        setCountry(response.data.user.address.country);
        setCity(response.data.user.address.city);
        setStreet(response.data.user.address.street);
        setNumber(response.data.user.address.number);
        setZipcode(response.data.user.address.zipcode);
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = async () => {
    try {
        setUserData({full_name, email, password, address: {country, city, street, number, zipcode}});
      await axios.put(`http://localhost:6500/user/${userData.email}`, userData);
      alert('User data updated successfully');
      setEditMode(false);
    } catch (error) {
      console.error('Error updating user data:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if(name === 'address') {
        userData.address = value;
        setUserData(userData);
    } 
    else if(name === 'country') {
        userData.address.country = value;
        setUserData(userData);
    } 
    else if(name === 'city') {
        userData.address.city = value;
        setUserData(userData);
    } 
    else if(name === 'street') {
        userData.address.street = value;
        setUserData(userData);
    } 
    else if(name === 'number') {
        userData.address.number = value;
        setUserData(userData);
    } 
    else if(name === 'zipcode') {
        userData.address.zipcode = value;
        setUserData(userData);
    } 
    else if(name === 'full_name') {
        userData.full_name = value;
        setUserData(userData);
    } 
    else if(name === 'email') {
        userData.email = value;
        setUserData(userData);
    } 
    else if(name === 'password') {
        userData.password = value;
        setUserData(userData);
    }
  };



  return (
    <div>
      <Navbar />
      <div>
        <h1>Personal Information</h1>
        <div>
          <form>
            <label htmlFor="fullName">Full Name:</label>
            <input
              type="text"
              id="full_name"
              name="full_name"
              value={userData.full_name}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="email">Email:</label>
            <input
              type="text"
              id="email"
              name="email"
              value={userData.email}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="password">Password:</label>
            <input
              type="text"
              id="password"
              name="password"
              value={userData.password}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="country">Country:</label>
            <input
              type="text"
              id="country"
              name="country"
              value={userData.address.country}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="city">City:</label>
            <input
              type="text"
              id="city"
              name="city"
              value={userData.address.city}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="street">Street:</label>
            <input
              type="text"
              id="street"
              name="street"
              value={userData.address.street}
              onChange={handleInputChange}
              disabled={!editMode}
            />
            <label htmlFor="number">Number:</label>
            <input
              type="text"
              id="number"
              name="number"
              value={userData.address.number}
              onChange={handleInputChange}
              disabled={!editMode}/>
            <label htmlFor="zipcode">Zipcode:</label>
            <input
              type="text"
              id="zipcode"
              name="zipcode"
              value={userData.address.zipcode}
              onChange={handleInputChange}
              disabled={!editMode}/>
          </form>
          {editMode ? (
            <button onClick={handleSaveClick}>Save</button>
          ) : (
            <button onClick={handleEditClick}>Edit</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default PersonalInformation;
