// Importing necessary dependencies
import React, { useState } from "react";

// Component for the first page of the login section
function FirstPage() {
  const [type, setType] = useState("signIn");

  // Function to handle button click and update the type state
  const handleOnClick = text => {
    if (text !== type) {
      setType(text);
      return;
    }
  };

  // CSS class for the container based on the type state
  const containerClass =
    "container " + (type === "signUp" ? "right-panel-active" : "");

  return (
    <div className="App">
      <div className={containerClass} id="container">
        {/* Register component */}
        <Register />

        {/* Login component */}
        <Login />

        <div className="overlay-container">
          <div className="overlay">
            {/* Overlay panel for sign in */}
            <div className="overlay-panel overlay-left">
              <h1>Welcome Back!</h1>
              <p>
                To keep connected with us please login with your personal info
              </p>
              <button
                className="ghost"
                id="signIn"
                onClick={() => handleOnClick("signIn")}
              >
                Sign In
              </button>
            </div>

            {/* Overlay panel for sign up */}
            <div className="overlay-panel overlay-right">
              <h1>Hello, Friend!</h1>
              <p>Enter your personal details and start journey with us</p>
              <button
                className="ghost "
                id="signUp"
                onClick={() => handleOnClick("signUp")}
              >
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FirstPage;
