import React, { useContext, useEffect, useState } from 'react';
import Navbar from '../../components/navbar';
import axios from 'axios';
import { Container, Row, Col } from 'react-bootstrap';
import "./product.css";
import { useParams } from 'react-router-dom';
import { ShopContext } from '../../context/shop-context'; // Import ShopContext

const Product = () => {
  const { category, index } = useParams(); // Get category and index from URL params
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [descp, setDescription] = useState('');
  const [image, setImage] = useState('');
  const [productData, setProductData] = useState({});

  const { addToCart } = useContext(ShopContext); // Access addToCart function from context

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:6500/${category}/${index}`);
        setProductData(response.data);
        setName(productData.name); 
        setPrice(productData.price); 
        setDescription(productData.description); 
        setImage(productData.image);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    fetchProduct();  
  }, [category, index]); // Add category and index to the dependency array

  const handleAddToCart = () => {
    addToCart(productData); // Call addToCart function from context
  };

  return (
    <div>
      <Navbar />
      <Container>
        <Row>
          <Col>
            <h1>{name}</h1>
            <img src={image} alt={name} />
            <p>{descp}</p>
            <h2>{price} $</h2>
            <button className='buttonadd' onClick={handleAddToCart}>Add to Cart</button>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Product;
