import React, { useEffect, useState } from 'react';
import Navbar from '../../components/navbar'; // Assuming you have Navbar component
import { Link } from 'react-router-dom';
import "./iphones.css";
import axios from 'axios';

const Iphones = () => {
  const [iphones, setIphones] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:6500/products/iphones');
        setIphones(response.data.products); 
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();  
  }, []);

  return (
    <div>
      <Navbar />
      <div className="search-container">
        <input type="text" placeholder="Search..." />
        <button>Search</button>
      </div>
      <div className="filter-container">
        <form>
          <label htmlFor="from">From:</label>
          <input type="text" id="from" name="from" />
        </form>
        <form>
          <label htmlFor="to">To:</label>
          <input type="text" id="to" name="to" />
        </form>
      </div>
      <div className="products">
        <div className="product-slide">
          {iphones.map((product, index) => ( // Use index as key
            <Link to={product.link} key={index}>
              <div className="product-card">
                <img src={product.image} alt={product.name} />
                <h3>{product.name}</h3>
                <p>${product.price}</p>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Iphones;
