import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../../components/navbar';
import './checkout.css';
import axios from 'axios';

const Checkout = () => {
  const [fullName, setFullName] = useState('');
  const [phone, setPhone] = useState('');
  const [country, setCountry] = useState('');
  const [city, setCity] = useState('');
  const [CreditCardNumber, setCreditCardNumber] = useState('');
  const [street, setStreet] = useState('');
  const [number, setNumber] = useState('');
  const [apt, setApt] = useState('');
  const [CVV, setCVV] = useState('');
  const [email, setEmail] = useState('');
  const [ValidDate, setValidDate] = useState('');

  const navigate = useNavigate();

  const handlePayment = async () => {
    if (!fullName || !phone || !country || !city || !street || !number || !CreditCardNumber || !ValidDate || !CVV || !email) {
      alert('Please fill in all required fields.');
      return;
    }
    if (CVV.length !== 3) {
      alert('Please enter a valid CVV.');
      return;
    }
    const creditCardRegex = /^\d{16}$/;
    if (!creditCardRegex.test(CreditCardNumber)) {
      alert('Please enter a valid credit card number.');
      return;
    }
    // Perform payment processing logic here
    try {
      const response = await axios.post('http://localhost:6500/order', {
        fullName,
        phone,
        country,
        city,
        street,
        number,
        apt,
        CreditCardNumber,
        ValidDate,
        CVV,
        email
      });
      alert('Order submitted');
      navigate('/shop');
    } catch (error) {
      console.error('Error submitting order:', error);
      alert('An error occurred while submitting the order. Please try again later.');
    }
  };

  return (
    <div>
      <Navbar />
      <h2>Checkout</h2>
      <form className="checkout-form">
        <label htmlFor="fullName" className="checkout-label">Full Name:</label>
        <input
          type="text"
          id="fullName"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          className="checkout-input"
        />
        <label htmlFor="email" className="checkout-label">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="checkout-input"
        />
        {/* Other input fields */}
        <button type="submit" onClick={handlePayment} className="checkout-input checkout-submit-button">
          Make a Payment
        </button>
      </form>
    </div>
  );
};

export default Checkout;
