import React, { useContext, useEffect, useState } from "react";
import { ShopContext } from "../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./cart.css";

export const Cart = () => {
  const { cartItems, getTotalCartAmount, checkout } = useContext(ShopContext);
  const [buyprod, setBuyprod] = useState([]);
  const totalAmount = getTotalCartAmount();
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);

  useEffect(() => {
      axios.get("http://localhost:6500/products")
        .then((res) => {
          const fetchedProducts = res.data;
          setProducts(fetchedProducts);
          const defaultCart = getDefaultCart(fetchedProducts);
          setCartItems(defaultCart);
        })
        .catch((error) => {
          console.error("Error fetching products:", error);
        });
    console.log(products)
    const purchasedProducts = products.filter(product => cartItems[product.prod_id] > 0);
    setBuyprod(purchasedProducts);
  }, [cartItems, products]);

  return (
    <div className="cart">
      <div>
        <h1>Your Cart Items</h1>
      </div>
      <div className="cart">
        {buyprod.map((product) => {
          return <CartItem data={product} key={product.prod_id} />;
        })}
      </div>

      {totalAmount > 0 ? (
        <div className="checkout">
          <p> Subtotal: ${totalAmount} </p>
          <button onClick={() => navigate("/shop")}> Continue Shopping </button>
          <button
            onClick={() => {
              checkout();
              navigate("/checkout");
            }}
          >
            {" "}
            Checkout{" "}
          </button>
        </div>
      ) : (
        <h1> Your Shopping Cart is Empty</h1>
      )}
    </div>
  );
};
