import React, { useContext, useEffect, useState } from "react";
import { ShopContext } from "../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./cart.css";
import axios from "axios";

export const Cart = () => {
  const { cartItems, getTotalCartAmount, checkout, products } = useContext(ShopContext);
  const [buyprod, setBuyprod] = useState([]);
  const totalAmount = getTotalCartAmount();
  const navigate = useNavigate();

  useEffect(() => {
    // Filter the products array to get the products that are in the cart
    const purchasedProducts = products.filter(product => cartItems[product.prod_id] > 0);
    setBuyprod(purchasedProducts);
  }, [cartItems, products]);

  return (
    <div className="cart">
      <div>
        <h1>Your Cart Items</h1>
      </div>
      <div className="cart">
        {buyprod.map((product) => {
          return <CartItem data={product} key={product.prod_id} />;
        })}
      </div>

      {totalAmount > 0 ? (
        <div className="checkout">
          <p> Subtotal: ${totalAmount} </p>
          <button onClick={() => navigate("/shop")}> Continue Shopping </button>
          <button
            onClick={() => {
              checkout();
              navigate("/checkout");
            }}
          >
            {" "}
            Checkout{" "}
          </button>
        </div>
      ) : (
        <h1> Your Shopping Cart is Empty</h1>
      )}
    </div>
  );
};
