import React from "react";
import axios from "axios";

function Register() {
  const [state, setState] = React.useState({
    name: "",
    email: "",
    password: "",
    country: "",
    city: "",
    street: "",
    number: "",
    apt: ""
  });

  const handleChange = (evt) => {
    const { name, value } = evt.target;
    setState({
      ...state,
      [name]: value
    });
  };

  const handleOnSubmit = async (evt) => {
    evt.preventDefault();

    try {
      const response = await axios.post("http://localhost:6500/register", state);
      console.log(response.data); // Log response from the server
      // Clear the form after successful submission
      setState({
        name: "",
        email: "",
        password: "",
        country: "",
        city: "",
        street: "",
        number: "",
        apt: ""
      });
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="form-container sign-up-container">
      <form onSubmit={handleOnSubmit}>
        <h1>Create Account</h1>
        <input
          type="text"
          name="name"
          value={state.name}
          onChange={handleChange}
          placeholder="Name"
        />
        <input
          type="email"
          name="email"
          value={state.email}
          onChange={handleChange}
          placeholder="Email"
        />
        <input
          type="password"
          name="password"
          value={state.password}
          onChange={handleChange}
          placeholder="Password"
        />
        <input
          type="text"
          name="country"
          value={state.country}
          onChange={handleChange}
          placeholder="Country"
        />
        <input
          type="text"
          name="city"
          value={state.city}
          onChange={handleChange}
          placeholder="City"
        />
        <input
          type="text"
          name="street"
          value={state.street}
          onChange={handleChange}
          placeholder="Street"
        />
        <input
          type="text"
          name="number"
          value={state.number}
          onChange={handleChange}
          placeholder="Apartment Number"
        />
        <input
          type="text"
          name="apt"
          value={state.apt}
          onChange={handleChange}
          placeholder="Apt"
        />
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}

export default Register;
