import React, { useContext, useEffect, useState } from "react";
import { ShopContext } from "../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./cart.css";
import Navbar from "./navbar";
import { PayPalButton } from 'react-paypal-button-v2';

export const Cart = () => {


  return (
    <div>
      <Navbar />
      <div className="cart">
        <div>
          <h1>Your Cart Items</h1>
        </div>
        <div className="cart">
          {buyprod.map((product) => (
            <CartItem data={product} quantity={1} key={product.prod_id} />
          ))}
        </div>
        {buyprod.length > 0 ? (
          <div className="checkout">
            <p>Subtotal: ${subtotal}</p>
            <div className="buttons-container">
              <button className="continue-shopping" onClick={() => navigate("/shop")}>Continue Shopping</button>
              <button className="checkout-button" onClick={handleCheckout}>Checkout</button>
            </div>
            <PayPalButton
                amount={subtotal}
                currency="USD"
                onSuccess={(details, data) => {
                    alert("Transaction completed by " + details.payer.name.given_name);
                }}
                options={{
                    clientId: "AUw64R1RHWdRKv_e7P92hTsbT9gA8L5HZVFjhG-qM0jUhpYqFmj3B2XImLaftht90qgxQTZFzRH1rXML"
                }}/>
          </div>
        ) : (
          <h1>Your Shopping Cart is Empty</h1>
        )}
      </div>
    </div>
  );
};
