import React, { useContext, useEffect, useState } from "react";
import { ShopContext } from "../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./cart.css";
import Navbar from "./navbar";;

export const Cart = () => {
  const { cartItems, getTotalCartAmount, checkout, products } = useContext(ShopContext);
  const [buyprod, setBuyprod] = useState([]);
  const totalAmount = getTotalCartAmount();
  const navigate = useNavigate();
  const [Subtotal, setSubtotal] = useState(0);

  useEffect(() => {
    const purchasedProducts = products.filter((product) => {
      return cartItems.includes(product.prod_id);
    });
    setBuyprod(purchasedProducts);
    let subtotal = 0;
    buyprod.forEach(pro => {
        subtotal += pro.price;
      });
    setSubtotal(Subtotal);
  }, [cartItems, products]);
  return (
    <div>
    < Navbar/>
    <div className="cart">
      <div>
        <h1>Your Cart Items</h1>
      </div>
      <div className="cart">
        {buyprod.map((product) => {
          return <CartItem data={product} key={product.prod_id} />;
        })}
      </div>

      {totalAmount > 0 ? (
        <div className="checkout">
          <p> Subtotal: ${getSubtotal} </p>
          <button onClick={() => navigate("/shop")}> Continue Shopping </button>
          <button
            onClick={() => {
              checkout();
              navigate("/checkout");
            }}
          >
            {" "}
            Checkout{" "}
          </button>
        </div>
      ) : (
        <h1> Your Shopping Cart is Empty</h1>
      )}
    </div>
    </div>
  );
};
