import React, { useEffect, useState } from 'react';
import Navbar from '../../components/navbar'; // Assuming you have Navbar component
import { Link } from 'react-router-dom';
import "./iphones.css";
import axios from 'axios';
import { Col, Container, Row } from 'react-bootstrap';

const Iphones = () => {
  const [iphones, setIphones] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:6500/products/iphones');
        setIphones(response.data.products); 
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();  
  }, []);

  return (
    <Container>
      <Row>
        <Col>
          <Navbar />
        </Col>
      </Row>
      <Row>
        <Col>
          <div className="iphones-search-container">
            <input type="text" placeholder="Search..." />
            <button>Search</button>
          </div>
        </Col>
      </Row>
      <Row>
        <Col>
          <div className="iphones-form-container">
            <div className="filter-container">
              <form>
                <label htmlFor="from">From:</label>
                <input type="text" id="from" name="from" />
              </form>
              <form>
                <label htmlFor="to">To:</label>
                <input type="text" id="to" name="to" />
              </form>
            </div>
          </div>
        </Col>
      </Row>
      <div className="products">
        <div className="product-slide">
          {iphones.map((product, index) => (
            <Link to={product.link} key={index}>
              <div className="product-card">
                <img src={product.image} alt={product.name} />
                <h3>{product.name}</h3>
                <p>${product.price}</p>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </Container>
  );
};

export default Iphones;
