// PersonalInformation.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/navbar';

const PersonalInformation = () => {
  const [userData, setUserData] = useState({});
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    const userEmail = getCookie('email');
    if (userEmail) {
      fetchUserData(userEmail);
    }
  }, []);

  const fetchUserData = async (email) => {
    try {
      const response = await axios.get(`http://localhost:6500/userdata/${email}`);
      setUserData(response.data);
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = async () => {
    try {
      await axios.put(`http://localhost:6500/userdata/${userData.email}`, userData);
      setEditMode(false);
    } catch (error) {
      console.error('Error updating user data:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  return (
    <div>
      <Navbar />
      <div>
        <h1>Personal Information</h1>
        <div>
          <p>Name: {editMode ? <input type="text" name="name" value={userData.name} onChange={handleInputChange} /> : userData.name}</p>
          <p>Email: {userData.email}</p>
          {/* Add more fields for other user data */}
        </div>
        {editMode ? (
          <button onClick={handleSaveClick}>Save</button>
        ) : (
          <button onClick={handleEditClick}>Edit</button>
        )}
      </div>
    </div>
  );
};

export default PersonalInformation;
