import React, { useEffect, useState } from 'react';
import Navbar from '../../components/navbar';
import axios from 'axios';
import { Container, Row, Col } from 'react-bootstrap';
import "./product.css";

const Product = ({ category, index }) => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [descp, setDescription] = useState('');
  const [image, setImage] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
        console.log(category, index);
      if (!category || !index) {
        setError("Invalid category or index");
        return;
      }

      try {
        const response = await axios.get(`http://localhost:6500/${category}/${index}`);
        const productData = response.data;

        setName(productData.name); 
        setPrice(productData.price); 
        setDescription(productData.description); 
        setImage(productData.image);
        setError(null); // Clear any previous errors
      } catch (error) {
        console.error('Error fetching product:', error);
        setError("Error fetching product");
      }
    };

    fetchProduct();  
  }, [category, index]); // Add category and index to the dependency array

  if (error) {
    return (
      <div>
        <Navbar />
        <Container>
          <Row>
            <Col>
              <h1>Error</h1>
              <p>{error}</p>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }

  return (
    <div>
      <Navbar />
      <Container>
        <Row>
          <Col>
            <h1>{name}</h1>
            <img src={image} alt={name} />
            <p>{descp}</p>
            <h2>{price}</h2>
            <button>Add to Cart</button>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Product;
