import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import emailjs from '@emailjs/browser';

const ForgotPassword = () => {
    const [code,Setcode] = useState('');
    const [email,SetEmail] = useState('');
    const codes = '';
    const navigate = useNavigate();
    const handelcon = (e) => {
        emailjs.init("98g7Qzscyfz-S-J7p");
        const serviceId = "service_lxiaq84";
        const templateId = "template_kzggrep";
        emailjs.send(serviceId, templateId, {
            email: "",
        });
        alert('conformation code sent, please check your email')
    }
    const handelSignin = (e) => {
        alert('conformation code verified')
        navigate('/');
    }
    const handeleChange = (e) => {
        if(e.target.name === 'email')
        {
            SetEmail(e.target.value);
        }
        if(e.target.name === 'code')
        {
            Setcode(e.target.value);
        }
    }
  return (
    <div>
        <div style={{marginTop:'20rem'}}>
            <form onSubmit={handelcon}>
                <input type="text" placeholder='enter your email' onChange={{handeleChange}} name='email' />
                <button style={{marginTop:'1rem'}}>send conformation code</button>
            </form>
        </div>
        <div style={{marginTop:'2.5rem'}}>
            <form onSubmit={handelSignin}>
                <input type="text" placeholder='enter conformation code ' name='code'/>
                <button style={{marginTop:'1rem'}}>submit</button>
            </form>
        </div>
    </div>
  );
};

export default ForgotPassword;
