import React, { useContext, useEffect, useState } from "react";
import { ShopContext } from "../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./cart.css";
import Navbar from "./navbar";

export const Cart = () => {
  const { cartItems, checkout, products } = useContext(ShopContext);
  const [buyprod, setBuyprod] = useState([]);
  const navigate = useNavigate();
  const [subtotal, setSubtotal] = useState(0);

  useEffect(() => {
    const purchasedProducts = products.filter((product) => {
      return cartItems.hasOwnProperty(product.prod_id);
    });
    setBuyprod(purchasedProducts);
    let subTotal = 0;
    purchasedProducts.forEach((product) => {
      // Multiply the price by the quantity of the item in the cart
      subTotal += product.price * cartItems[product.prod_id];
    });
    setSubtotal(subTotal);
  }, [cartItems, products]);

  return (
    <div>
      <Navbar />
      <div className="cart">
        <div>
          <h1>Your Cart Items</h1>
        </div>
        <div className="cart">
          {buyprod.map((product) => {
            // Pass the quantity from cartItems to the CartItem component
            const quantity = cartItems[product.prod_id] || 0;
            return <CartItem data={product} quantity={quantity} key={product.prod_id} />;
          })}
        </div>
        {subtotal > 0 ? (
          <div className="checkout">
            <p> Subtotal: ${subtotal} </p>
            <button onClick={() => navigate("/shop")}> Continue Shopping </button>
            <button
              onClick={() => {
                checkout();
                navigate("/checkout");
              }}
            >
              {" "}
              Checkout{" "}
            </button>
          </div>
        ) : (
          <h1> Your Shopping Cart is Empty</h1>
        )}
      </div>
    </div>
  );
};
