import React, { useContext, useEffect, useState } from "react";
import { ShopContext } from "../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./cart.css";
import Navbar from "./navbar";

export const Cart = () => {
  const { cartItems, products } = useContext(ShopContext);
  const [buyprod, setBuyprod] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const navigate = useNavigate();
  const [subtotal, setSubtotal] = useState(0);

  useEffect(() => {
    // Filter products that are in the cart
    const purchasedProducts = products.filter((product) => {
      return cartItems.hasOwnProperty(product.prod_id) && cartItems[product.prod_id] > 0;
    });
    setBuyprod(purchasedProducts);

    // Calculate subtotal
    let subtotal = 0;
    purchasedProducts.forEach((product) => {
      subtotal += product.price * cartItems[product.prod_id];
    });
    setSubtotal(subtotal);

    // Set dataLoaded to true when the data is loaded
    setDataLoaded(true);
  }, [cartItems, products]);

  const handleCheckout = () => {
    if (dataLoaded) {
      navigate('/checkout', { state: { buyprod } });
      setSubtotal(0);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="cart">
        <div>
          <h1>Your Cart Items</h1>
        </div>
        <div className="cart">
          {buyprod.map((product) => (
            <CartItem data={product} quantity={1} key={product.prod_id} />
          ))}
        </div>
        {buyprod.length > 0 ? (
          <div className="checkout">
            <p> Subtotal: ${subtotal} </p>
            <div className="buttons-container">
              <button className="continue-shopping" onClick={() => navigate("/shop")}> Continue Shopping </button>
              <button className="checkout-button" onClick={handleCheckout}> Checkout </button>
            </div>
          </div>
        ) : (
          <h1> Your Shopping Cart is Empty</h1>
        )}
      </div>
    </div>
  );
};
