import axios from "axios";
import React, { createContext, useEffect, useState } from "react";

export const ShopContext = createContext(null);

export const ShopContextProvider = (props) => {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    fetchProducts(); 
    const interval = setInterval(fetchProducts, 60000); 

    return () => clearInterval(interval);
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get("http://localhost:6500/products");
      const fetchedProducts = response.data.products;
      setProducts(fetchedProducts);

      fetchedProducts.forEach(product => {
        if (product.notify && product.notify.length > 0) {
          product.notify.forEach(email => {
            console.log(`Sending email to ${email} for product ${product.name}`);
          });

          product.notify = [];
          updateProduct(product._id, product);
        }
      });
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  const updateProduct = async (productId, updatedProductData) => {
    try {
      await axios.put(`http://localhost:6500/products/${productId}`, updatedProductData);
    } catch (error) {
      console.error("Error updating product:", error);
    }
  };

  const getTotalCartAmount = () => {
    let totalAmount = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        let itemInfo = products.find((product) => product.prod_id === Number(item));
        totalAmount += cartItems[item] * itemInfo.price;
      }
    }
    return totalAmount;
  };

  const addToCart = (itemId) => {
    setCartItems((prevCartItems) => {
      const updatedCartItems = { ...prevCartItems };
      updatedCartItems[itemId] = updatedCartItems[itemId] ? updatedCartItems[itemId] + 1 : 1;
      return updatedCartItems;
    });
    alert("Item added to cart");
  };




  const removeFromCart = (itemId) => {
    setCartItems((prevCartItems) => {
      const updatedCartItems = { ...prevCartItems };
      updatedCartItems[itemId] = updatedCartItems[itemId] ? updatedCartItems[itemId] - 1 : 0;
      return updatedCartItems;
    });
  };

  const updateCartItemCount = (newAmount, itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: newAmount }));
  };

  const checkout = () => {
    setCartItems([]);
  };

  const contextValue = {
    cartItems,
    addToCart,
    updateCartItemCount,
    removeFromCart,
    getTotalCartAmount,
    checkout,
    products,
  };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};
