import React, { useContext, useEffect, useState } from "react";
import { ShopContext } from "../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./cart.css";
import Navbar from "./navbar";
import { PayPalButton } from 'react-paypal-button-v2';

export const Cart = () => {
  const { cartItems, products } = useContext(ShopContext);
  const [buyprod, setBuyprod] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const navigate = useNavigate();
  const [subtotal, setSubtotal] = useState(0);

  useEffect(() => {
    // Filter products that are in the cart
    const purchasedProducts = products.filter((product) => {
      return cartItems.hasOwnProperty(product.prod_id) && cartItems[product.prod_id] > 0;
    });
    setBuyprod(purchasedProducts);

    // Calculate subtotal
    let total = 0;
    purchasedProducts.forEach((product) => {
      total += product.price * cartItems[product.prod_id];
    });
    setSubtotal(total);

    // Set dataLoaded to true when the data is loaded
    setDataLoaded(true);
  }, [cartItems, products]);

  const handleCheckout = () => {
    if (dataLoaded) {
      navigate('/checkout', { state: { buyprod } });
    }
  };

  return (
    <div>

            <PayPalButton
                amount={subtotal}
                currency="USD"
                onSuccess={(details, data) => {
                    alert("Transaction completed by " + details.payer.name.given_name);
                }}
                options={{
                    clientId: "AUw64R1RHWdRKv_e7P92hTsbT9gA8L5HZVFjhG-qM0jUhpYqFmj3B2XImLaftht90qgxQTZFzRH1rXML"
                }}/>
      </div>)
};
