import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { ShopContext } from '../../context/shop-context'; // Import ShopContext

function Login(props) {
  const { setemailv, email } = useContext(ShopContext); // Access setemail function from context
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    setemailv(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    if(email === '' || password === '') return alert('Please fill in all fields');
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:6500/login', { email, password });
      if (response.data.success) {
        navigate("/shop", { replace: true });
    } else {
        setMessage('Invalid credentials');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error logging in. Please try again.');
    }
  };

  return (
    <div className="form-container sign-in-container">
      <form onSubmit={handleSubmit}>
        <h1 className='titlee'>Sign in</h1>
        <input
          type="email"
          placeholder="Email"
          name="email"
          value={email}
          onChange={handleEmailChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={password}
          onChange={handlePasswordChange}
        />
        <button>Sign In</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default Login;
