import React, { useEffect, useState } from 'react';
import Navbar from '../../components/navbar'; // Assuming you have Navbar component
import { Link } from 'react-router-dom';
import "./iphones.css";
import axios from 'axios';
import { Col, Container, Row } from 'react-bootstrap';

const Iwatches = () => {
  const [iwatches, setIwatches] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:6500/products/iwatches');
        setIwatches(response.data.products); 
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();  
  }, []);

  const handleSearch = () => {
    return iwatches.filter(product =>
      product.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
  };

  const handleFilter = () => {
    return iwatches.filter(product =>
      (!minPrice || parseInt(product.price) >= parseInt(minPrice)) &&
      (!maxPrice || parseInt(product.price) <= parseInt(maxPrice))
    );
  };

  const filteredProducts = iwatches.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesPriceRange =
      (!minPrice || parseInt(product.price) >= parseInt(minPrice)) &&
      (!maxPrice || parseInt(product.price) <= parseInt(maxPrice));
    return matchesSearch && matchesPriceRange;
  });

  return (
    <Container>
      <Row>
        <Col>
          <Navbar />
        </Col>
      </Row>
      <Row>
        <Col>
          <div className="iphones-search-container">
            <input 
              type="text" 
              placeholder="Search..." 
              value={searchQuery} 
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button onClick={handleSearch}>Search</button>
          </div>
        </Col>
      </Row>
      <Row>
        <Col>
          <div className="iphones-form-container">
            <div className="filter-container">
              <form>
                <label htmlFor="from">From:</label>
                <input 
                  type="text" 
                  id="from" 
                  name="from" 
                  value={minPrice} 
                  onChange={(e) => setMinPrice(e.target.value)}
                />
              </form>
              <form>
                <label htmlFor="to">To:</label>
                <input 
                  type="text" 
                  id="to" 
                  name="to" 
                  value={maxPrice} 
                  onChange={(e) => setMaxPrice(e.target.value)}
                />
              </form>
            </div>
          </div>
        </Col>
      </Row>
      <div className="products">
        <div className="product-slide">
          {filteredProducts.map((product, index) => (
            <Link to={product.link} key={index}>
              <div className="product-card">
                <img src={product.image} alt={product.name} />
                <h3>{product.name}</h3>
                <p>${product.price}</p>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </Container>
  );
};

export default Iwatches;
